mapper.select:
select

from finance_purchase_supplier_payrecords
where yn = 1

  and  supplier_no = #{supplierNo}


  and DATE_FORMAT(bill_time,'%Y-%m-%d')  <  #{startDate}


  and  org_code = #{orgCode}


  and  DATE_FORMAT(create_time,'%Y-%m-%d %H:%m:%s')  <=   DATE_FORMAT(#{createTime},'%Y-%m-%d %H:%m:%s')

order by id desc limit 1
